<template>
  <div class="main-content">
    <div class="pet-list">
      <div class="pet-item" v-for="pet in pets" :key="pet.id" @click="redirectToProfile">
        <img
          src="data:image/jpeg;base64,"
          alt="img-pet">
        <span>{{pet.pet_name}}</span>
      </div>
    </div>
  </div>
</template>

<script>
import axios from 'axios'

export default{

  data(){
    return{
      pets:[]
    }
  },
  methods:{
    redirectToProfile(){
      this.$router.push("/perfil")
    }
  },

  mounted(){
    axios.get("http://localhost:8000/api/pets/adocao")
    .then((response)=>{
      this.pets = response.data
    })
  }
}
</script>

